@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;600&display=swap');

/* Global Styles */
body {
  background: linear-gradient(to right, #fceabb, #f8b500);
  font-family: 'Poppins', sans-serif;
  padding: 20px;
  margin: 0;
  color: #111827;
}

/* Card Container */
.card {
  max-width: 550px;
  margin: 30px auto;
  padding: 25px;
  background: white;
  border-radius: 16px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  position: relative;
  overflow: hidden;
}

/* Header Gradient Stripe */
.card::before {
  content: '';
  display: block;
  height: 60px;
  background: linear-gradient(135deg, #ff6b6b, #f06595, #cc5de8);
  margin: -25px -25px 30px -25px;
  border-radius: 16px 16px 0 0;
}

/* Headings */
.card h1, .card h2, .form-header {
  text-align: center;
  color: #1f2937;
  font-weight: 600;
  font-size: 2em;
  margin: 0 0 15px;
}

/* Paragraphs */
.card p {
  text-align: center;
  color: #555;
  font-size: 1.1em;
  margin-bottom: 25px;
}

/* Form Wrapper */
.inputs-wrapper {
  margin-top: 10px;
}

.form-group {
  margin-bottom: 18px;
}

/* Input Fields */
.card input,
.card textarea,
.card select {
  width: 100%;
  padding: 14px 16px;
  font-size: 1rem;
  border: 2px solid #d1d5db;
  border-radius: 10px;
  background: #fefefe;
  transition: border-color 0.3s ease, box-shadow 0.3s ease;
  box-shadow: inset 0 0 4px rgba(0, 0, 0, 0.04);
}

/* Focus State */
.card input:focus,
.card textarea:focus,
.card select:focus {
  border-color: #8b5cf6;
  box-shadow: 0 0 0 3px rgba(139, 92, 246, 0.2);
  outline: none;
}

/* Placeholder Text */
.card input::placeholder,
.card textarea::placeholder {
  color: #9ca3af;
  font-style: italic;
}

/* Submit Button */
.card button[type="submit"] {
  background: linear-gradient(to right, #34d399, #3b82f6, #8b5cf6);
  color: white;
  font-size: 1.1rem;
  padding: 14px;
  border: none;
  border-radius: 12px;
  cursor: pointer;
  font-weight: 600;
  transition: all 0.3s ease;
  width: 100%;
  box-shadow: 0 4px 15px rgba(139, 92, 246, 0.3);
}

.card button[type="submit"]:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(59, 130, 246, 0.4);
}

/* Error Message (hidden by default) */
.error-hidden {
  visibility: hidden;
  height: 0;
}

/* Mobile Responsive */
@media (max-width: 768px) {
  .card {
    padding: 16px;
    margin: 10px;
  }

  .card h1, .card h2 {
    font-size: 1.6em;
  }

  .card::before {
    height: 50px;
    margin: -16px -16px 25px -16px;
  }
}

